settings.customcss:
/* YouTube.com Page */
:root {
  --yt-img-border-radius: 12px;
  --yt-regular-border-radius: 12px;
  --yt-brand-medium-red: #ef4141;
  --transition-all: all 0.15s ease-in-out;
  --sans-font: "YouTube Sans";
  --spacing: 0px;
  --ytd-miniplayer-playlist-height: 385px;
}

* {
  font-family: var(--sans-font) !important;
  letter-spacing: var(--spacing) !important;
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */
}

/* Scrollbar */
*::-webkit-scrollbar {
  display: none;
}

/* Disable continuous page scroll */
/* And only show first 12 cards on home feed */
ytd-two-column-browse-results-renderer[page-subtype="home"]
  #primary
  #contents
  .ytd-rich-grid-renderer:nth-of-type(n + 13),
ytd-two-column-browse-results-renderer[page-subtype="home"]
  #primary
  #contents
  ytd-continuation-item-renderer,
ytd-feed-filter-chip-bar-renderer,
ytd-two-column-browse-results-renderer[page-subtype="subscriptions"]
  #primary
  #contents
  .ytd-rich-grid-renderer:nth-of-type(n + 13),
ytd-two-column-browse-results-renderer[page-subtype="subscriptions"]
  #primary
  #contents
  ytd-continuation-item-renderer {
  display: none !important;
}

/* Hide other important parts */
#endpoint[title="Shorts"],
#footer,
#primary #sections.ytd-guide-renderer > .ytd-guide-renderer:nth-child(n + 3),
#sections > ytd-guide-section-renderer:nth-child(n + 3),
.ytd-rich-section-renderer[is-shorts],
.ytd-statement-banner-renderer,
ytd-feed-filter-chip-bar-renderer,
ytd-rich-section-renderer.ytd-rich-grid-renderer {
  display: none !important;
}

/* Remove padding at the top of the page */
#contents.ytd-rich-grid-renderer,
ytd-rich-grid-renderer[top-row-endorsement] #contents.ytd-rich-grid-renderer {
  padding-top: 5px;
}

ytd-masthead[frosted-glass="with-chipbar"] #background.ytd-masthead {
  height: 56px;
}

/* Home video title */
#video-title.ytd-rich-grid-media {
  font-size-adjust: 0.45;
  font-weight: normal;
}

/* ---------------------------------------------------------------- */
/* YouTube Video Page */
/* ---------------------------------------------------------------- */
#teaser-carousel {
  display: none;
}

#masthead-container:has(#masthead[is-watch-page]) {
  transform: translate(0%, -50%);
  opacity: 0;
  transition: var(--transition-all);
}

#masthead-container:has(#masthead[is-watch-page]):hover {
  transform: translate(0);
  opacity: 1;
}

#page-manager:has(ytd-watch-flexy[role="main"]) {
  margin-top: 0px !important;
}

h1.ytd-watch-metadata {
  justify-self: center;
}

#title h1 yt-formatted-string {
  font-size: 2.8rem;
}

/* Video chapters */
.ytp-time-display,
.ytp-chapter-container button {
  font-size-adjust: 0.52;
  font-weight: bold;
}

#columns {
  align-items: center;
}

/* Secondary - primary */
#related,
.ytp-fullerscreen-edu-chevron {
  display: none;
}

#columns #primary {
  width: 80% !important;
  max-width: 80% !important;
}

ytd-watch-flexy[flexy] #columns.ytd-watch-flexy {
  flex-direction: column !important;
  padding-right: 0 !important;
}

ytd-watch-flexy[flexy] #secondary.ytd-watch-flexy {
  padding-right: 0 !important;
  width: 70% !important;
}

/* Description */
#info-container.ytd-watch-info-text {
  justify-content: center;
  color: var(--yt-spec-call-to-action);
  margin-top: 7px;
  margin-bottom: 10px;
}

ytd-expander.ytd-video-secondary-info-renderer {
  margin: 0px;
}

#bottom-row > #description {
  border: 1px solid var(--yt-spec-10-percent-layer);
}

#description-inner {
  max-width: none !important;
  text-align: center;
  font-size-adjust: 0.56;
  overflow: scroll;
  max-height: var(--ytd-watch-flexy-panel-max-height);
}

#lockup-container.ytd-structured-description-playlist-lockup-renderer {
  align-items: center;
  justify-content: center;
  flex-direction: column;
}

#lockup-container #description {
  border: initial;
  background: transparent;
  min-height: auto;
  height: 10px;
  margin: 0;
}

#media-lockups > ytd-structured-description-playlist-lockup-renderer,
ytd-video-description-infocards-section-renderer,
ytd-video-description-transcript-section-renderer {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

ytd-merch-shelf-renderer {
  border-bottom: none !important;
}

/* Comment section */
ytd-continuation-item-renderer {
  display: initial;
}

#comments {
  width: 80%;
  margin: auto;
  margin-bottom: 10px;
  padding-top: 10px;
  padding-left: 10px;
  padding-bottom: 3px;
  border-radius: var(--yt-regular-border-radius);
  border: 1px solid var(--yt-spec-10-percent-layer);
  background: var(--yt-spec-badge-chip-background);
}

#comments #sections {
  overflow: scroll;
  height: 83px;
  transition: var(--transition-all) 1s;
}

#comments #sections:hover {
  height: var(--ytd-watch-flexy-panel-max-height);
}

#expander.ytd-comment-renderer {
  overflow: hidden;
}

#title.ytd-comments-header-renderer,
ytd-comments-header-renderer {
  margin-bottom: 10px;
  margin-top: 0px;
}
